import {
    Component,
    OnInit,
} from "@angular/core";
import { CommonModule } from "@angular/common";
import { FormControl, FormGroup, ReactiveFormsModule } from "@angular/forms";
import { ColorPickerComponent } from "../../../common/color-picker/color-picker.component";

@Component({
    selector: "todo-new",
    standalone: true,
    imports: [CommonModule, ReactiveFormsModule, ColorPickerComponent],
    templateUrl: "./new.component.html",
    styleUrls: ["./new.component.scss"],
})
export class NewComponent implements OnInit {


    public formGroup: FormGroup;
    public currentColor: string;
    public colors = [
        "#ff0000", // red
        "#00ff00", // green
        "#0000ff", // blue
        "#ffff00", // yellow
        "#ff00ff", // magenta
        "#00ffff", // cyan
    ];
    public ctrls = [
        { name: "title" },
        { name: "completed" },
        { name: "color" },
    ];

    constructor() {}

    public ngOnInit(): void {
        this.formMaker(this.ctrls);
    }

    private formMaker(ctrls): void {
        const obj = Object.assign(
            {},
            ...ctrls.map((ctrl) => ({
                [ctrl.name]: new FormControl(),
            }))
        );
        this.formGroup = new FormGroup(obj);
    }
}
<h1>New Todo</h1>

<form [formGroup]="formGroup">
    <div class="mb-3">
        <label for="title" class="form-label">title</label>
        <input type="text" class="form-control" formControlName="title" />
    </div>

    <div class="mb-3 form-check">
        <input
            type="checkbox"
            class="form-check-input"
            formControlName="completed"
        />
        <label class="form-check-label" for="completed">completed</label>
    </div>

    <color-picker [colors]="colors" formControlName="color"></color-picker>
</form>

